```js
// ---------- helpers ----------
function shuffled(arr) {
  const a = arr.slice();
  for (let i = a.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}
function allImages() {
  const mk = (prefix, n = 49) => Array.from({ length: n }, (_, i) => `${prefix}_${i + 1}.jpg`);
  return [...mk(man), ...mk(woman), ...mk(neutral)]; // 147 total
}
function toPublicPath(img) {
