    if (!p.get(shuffled_greetings)) {
      // per-player greeting order; extend if there are more rounds than greetings
      const order = shuffled(greetings).slice();
      while (order.length < totalRounds) order.push(...shuffled(greetings));
      p.set(shuffled_greetings, order.slice(0, totalRounds));
