  initPlayerDecks(player, game);
});

// one place for your per-player state
function initPlayerDecks(p, game) {
  if (p.get(__inited)) return;

  const { numRounds } = game.get(treatment) || {};
  const totalRounds = 2 * Number(numRounds || 0);

  // per-player image deck (no repeats within this player)
  const mk = (prefix, n = 49) => Array.from({ length: n }, (_, i) => `${prefix}_${i + 1}.jpg`);
  const all = [...mk(man), ...mk(woman), ...mk(neutral)];
  const deck = shuffled(all);
  p.set(imgs, deck.slice(0, Math.max(totalRounds, 1)));

  // per-player greeting order (long enough for all rounds)
  const order = shuffled(greetings).slice();
  while (order.length < totalRounds) order.push(...shuffled(greetings));
  p.set(shuffled_greetings, order.slice(0, totalRounds));

  // optional: stable avatar
  const first = (p.get(imgs) || [])[0];
  if (first) p.set(avatar_url, toPublicPath(first));

  p.set(__inited, true);
}
```

# Option B (portable): initialize at the start of the first round
This runs **before any stage** of Round 1, so it still happens “before they play.”

```js
