  const round = stage.currentRound;
  const roundIndex = Number(round.get(roundIndex) ?? 0); // ‚úÖ correct scope
  const attr = chatV2;

  for (const p of game.players) {
    // Pull this player's unique picks for THIS round
    const deck = p.get(image_deck) || [];
    const img = deck[roundIndex % deck.length]; // deck is sized to totalRounds, but % is safe
